INSERT INTO `wp2l_26_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 31, '2019-05-08 16:40:46', '2019-05-08 14:40:46', 'Welcome to <a href=\"http://jatopata.com/\">Mercado</a>. This is your first post. Edit or delete it, then start blogging!', '¡Hola mundo!', '', 'publish', 'open', 'open', '', 'hola-mundo', '', '', '2019-05-08 16:40:46', '2019-05-08 14:40:46', '', 0, 'http://jatopata.com/kocu/?p=1', 0, 'post', '', 1),
(2, 31, '2019-05-08 16:40:46', '2019-05-08 14:40:46', 'Esta es una página de ejemplo, Es diferente a una entrada de blog porque se mantiene estática y se mostrará en la barra de navegación (en la mayoría de temas). Casi todo el mundo comienza con una página Acerca de mí para presentarse a los potenciales visitantes. Podría ser algo así:\n\n<blockquote>¡Hola!: Soy mensajero por el día, aspirante a actor por la noche y esta es mi web. Vivo en Madrid, tengo perrazo llamado Juan y me encantan las piñas coladas (y que me pille un chaparrón)</blockquote>\n\n… o algo así:\n\n<blockquote>La empresa Calcetines XYZ se fundó en 1971, y ha estado produciendo calcetines de calidad para sus clientes desde entonces. Ubicada en Gothan, la empresa XYZ tiene más de 2.000 empleados e intenta ayudar en lo que puede para mejorar la vida en Gothan</blockquote>\n\nSi eres nuevo en WordPress deberías ir a <a href=\"http://jatopata.com/kocu/wp-admin/\">tu escritorio</a> para borrar esta página y crear algunas nuevas con tu contenido. ¡Pásalo bien!', 'Página de ejemplo', '', 'publish', 'closed', 'open', '', 'pagina-ejemplo', '', '', '2019-05-08 16:40:46', '2019-05-08 14:40:46', '', 0, 'http://jatopata.com/kocu/?page_id=2', 0, 'page', '', 0),
(3, 0, '2019-05-08 16:40:46', '2019-05-08 14:40:46', '[ns-add-product][ns-edit-product]', 'Agregar/Editar Producto', '', 'publish', 'closed', 'closed', '', 'agregar', '', '', '2019-05-08 16:40:46', '2019-05-08 14:40:46', '', 0, 'http://jatopata.com/kocu/agregar/', 0, 'page', '', 0),
(4, 0, '2019-05-08 16:40:46', '2019-05-08 14:40:46', '[ns-add-product][ns-edit-product]', 'Modificar Tienda', '', 'publish', 'closed', 'closed', '', 'modificar', '', '', '2019-05-08 16:40:46', '2019-05-08 14:40:46', '', 0, 'http://jatopata.com/kocu/modificar/', 0, 'page', '', 0),
(5, 2, '2019-06-19 00:13:55', '0000-00-00 00:00:00', '', 'Borrador automático', '', 'auto-draft', 'open', 'open', '', '', '', '', '2019-06-19 00:13:55', '0000-00-00 00:00:00', '', 0, 'http://jatopata.com/kocu/?p=5', 0, 'post', '', 0),
(6, 2, '2019-05-08 16:40:46', '2019-05-08 14:40:46', '', 'Remodelacion de Casa', '', 'draft', 'open', 'closed', '', 'remodelacion-de-casa', '', '', '2019-05-08 16:40:46', '2019-05-08 14:40:46', '', 0, 'http://jatopata.com/kocu/agregar/', 0, 'product', '', 0),
(7, 2, '2019-06-19 00:15:21', '2019-06-18 22:15:21', '', '70c69c33-cff5-4dea-a3b7-ccef5d91c2f8', '', 'inherit', 'open', 'closed', '', '70c69c33-cff5-4dea-a3b7-ccef5d91c2f8', '', '', '2019-06-19 00:15:21', '2019-06-18 22:15:21', '', 6, 'http://jatopata.com/kocu/wp-content/uploads/sites/26/2019/05/70c69c33-cff5-4dea-a3b7-ccef5d91c2f8.jpg', 0, 'attachment', 'image/jpeg', 0);

