(3224, 'forgot_password_link_mail_subject', 'Password Reset Request', 'yes'),
(3225, 'forgot_password_link_mail_body', 'Someone requested that the password be reset for the following account:\r\n<br>\r\n#site_url#\r\n<br>\r\nUsername: #user_name#\r\n<br>\r\nIf this was a mistake, just ignore this email and nothing will happen.\r\n<br>\r\nTo reset your password, visit the following address:\r\n<br>\r\n#resetlink#', 'yes'),
(3226, 'new_password_mail_subject', 'Password Reset Request', 'yes'),
(3227, 'new_password_mail_body', 'Your new password for the account at:\r\n<br>\r\n#site_url#\r\n<br>\r\nUsername: #user_name#\r\n<br>\r\nPassword: #user_password#\r\n<br>\r\nYou can now login with your new password at:\r\n#site_url#', 'yes'),
(3228, 'custom_style_ap', '.log_forms { width: 98%; padding: 5px; border: 1px solid #CCC; margin: 2px; box-sizing:border-box; } .log-form-group{ margin: 0px; width: 100%; padding:5px; } .log_forms input[type=text], input[type=password] { width: 100%; padding: 7px 0 7px 4px !important; border: 1px solid #E3E3E3; margin:0px !important; } .log_forms input[type=submit] { width: 100%; padding: 7px; border: 1px solid #7ac9b7; } .log_forms input[type=text]:focus, input[type=password]:focus { border-color: #4697e4; } .lw-error{ color:#ff0000; } input.lw-error{ border:1px solid #ff0000 !important; }', 'yes'),
(3229, 'widget_login_wid', 'a:1:{s:12:\"_multiwidget\";i:1;}', 'yes'),
(3230, 'new_user_register_mail_subject', 'Registration Successful', 'yes'),
(3231, 'new_user_register_mail_body', 'We are pleased to confirm your registration for #site_name#. Below is your login credential.\r\n&lt;br&gt;&lt;br&gt;\r\n&lt;strong&gt;Username&lt;/strong&gt; : #user_name#\r\n&lt;br&gt;\r\n&lt;strong&gt;Password&lt;/strong&gt; : #user_password#\r\n&lt;br&gt;\r\n&lt;strong&gt;Site Link&lt;/strong&gt; : #site_url#\r\n&lt;br&gt;&lt;br&gt;\r\nThank You', 'yes'),
